<span class="line"><span style="color:#89DDFF;">    </span><span style="color:#C792EA;">aria-controls</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">id-of-a-textbox</span><span style="color:#89DDFF;">&quot;</span></span>
